events.inlineCode:
//Get scene variables
var fnum = runtimeScene.getVariables().get("filenum").getAsString();

pickandplay(fnum);

// say a message
function pickandplay(fnum) {
    // get the sound manager 
    var sound_manager = runtimeScene.getGame().getSoundManager();
    // play random sound, channel = 20, loop = false, volume = 25, pitch = 1.2
    sound_manager.playSoundOnChannel(fnum + ".mp3", 20, false, 25, 1.2);

}
